import React,{useState, useEffect, useRef} from "react";
import Tree from "react-d3-tree";
import axios from "axios";
import defaultArray from './convertedArray';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import './custom-tree.css';
export default function CenteredTree () {
  
  const [channel, setChannel] = useState('Retail');
  const channels = defaultArray.filter(item => item.name === channel )
  const debugData = [
    {
      name :"SALAM DMS",
      children : channels
    }
   
  ];
  function linkColor(debugData) {
    switch (debugData)
    {
      case 'Retail': 
        return '#0000FF';//blue
        break;
      case 'Task Actions':
        return '#FF7F00';//orange
        break;
      case 'App Actions': 
      return '#FF0000';//red
      break;
      case 'Insight Actions':
      return '#7F007F';//purple
      break;
      case 'Meta Actions':
        return '#00FFFF';//cyan
        break;
      default:
        return '#0950D0';//generic blue
        break;
    }
  }
  const containerStyles = {
    width: "100%",
    height: "100vh"
  };
  
  const svgSquare = {
    shape: "rect",
    shapeProps: {
      width: 20,
      height: 20,
      x: -10,
      y: -10
    }
  };
  const handleChange = (event) => {
    setChannel(event.target.value);
  };
  console.log("linkColor",linkColor())
  const [translate, setTranslate] = useState({
    x: 0,
    y: 0
  })
  const treeContainer = useRef(null)
  useEffect(() => {
    const dimensions = treeContainer.current.getBoundingClientRect();
    setTranslate({
        x: dimensions.width / 10,
        y: dimensions.width / 3
    })
  }, []);

  return (
    
    <div style={containerStyles} ref={treeContainer}>
       <Box sx={{ maxWidth: 120 }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Select Channel</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={channel}
          label="Select Channel"
          onChange={handleChange}
        >
          
          {defaultArray.map(item => <MenuItem value={item.name}>{item.name}</MenuItem> )}
        </Select>
      </FormControl>
    </Box>
      <Tree
        data={debugData}
        translate={translate}
        orientation={"vertical"}
        nodeSvgShape={svgSquare}
        rootNodeClassName="node__root"
        branchNodeClassName={linkColor}
        leafNodeClassName="node__leaf"
        circleRadius={5}
      />
    </div>
  );
} 
