import React from "react";
import Tree from "react-d3-tree";
import axios from "axios";
import defaultArray from './array'
const directSalesChannel = defaultArray.filter(item => item.channelName === "Indirect Sales Channel");
const directSalesChannelList = directSalesChannel[0]?.subChannel?.map(item => {
  return{
    name: item.channelName
  }
})

const debugData = [
  {
    name :"SALAM DMS",
    children : [
     {
       name: "Indirect Sales Channel",
       children :[
        directSalesChannelList.map(item => item)
       ]
     }
    
    ]
  }
 
];
console.log("dataForRender", directSalesChannelList)
const containerStyles = {
  width: "100%",
  height: "100vh"
};

const svgSquare = {
  shape: "rect",
  shapeProps: {
    width: 20,
    height: 20,
    x: -10,
    y: -10
  }
};
const headers = defaultArray.map(item => {
  return{ 
    name : item.channelName != null ? item.channelName : "" ,
  }
})
console.log("headers", headers)
export default class CenteredTree extends React.PureComponent {
  state = {
    records : "",
    headerRecords: "",
  };

  componentDidMount() {
    const dimensions = this.treeContainer.getBoundingClientRect();
    this.setState({
      translate: {
        x: dimensions.width / 10,
        y: dimensions.width / 3
      }
    });
   }
  render() {
    {console.log("defaultArray",defaultArray)}
    return (
      <div style={containerStyles} ref={(tc) => (this.treeContainer = tc)}>
        <Tree
          data={debugData}
          translate={this.state.translate}
          orientation={"horizontal"}
          nodeSvgShape={svgSquare}
          circleRadius={5}
        />
      </div>
    );
  }
}
