import React,{useState, useEffect, useRef} from "react";
import Tree from "react-d3-tree";
import axios from "axios";
import defaultArray from './convertedArray';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
const channels = defaultArray.map(item => item.name)
console.log("defaultArray", channels)
const debugData = [
  {
    name :"SALAM DMS",
    children : defaultArray
  }
 
];
const containerStyles = {
  width: "100%",
  height: "100vh"
};

const svgSquare = {
  shape: "rect",
  shapeProps: {
    width: 20,
    height: 20,
    x: -10,
    y: -10
  }
};

export default function CenteredTree () {
  const [channel, setChannel] = useState('');

  const handleChange = (event) => {
    setChannel(event.target.value);
  };
  const [translate, setTranslate] = useState({
    x: 0,
    y: 0
  })
  const treeContainer = useRef(null)
  useEffect(() => {
    const dimensions = treeContainer.current.getBoundingClientRect();
    setTranslate({
        x: dimensions.width / 10,
        y: dimensions.width / 3
    })
  }, []);
  console.log("age", channel)
  return (
    
    <div style={containerStyles} ref={treeContainer}>
       <Box sx={{ maxWidth: 120 }}>
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Select Channel</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={channel}
          label="Select Channel"
          onChange={handleChange}
        >
          
          {defaultArray.map(item => <MenuItem value={item.name}>{item.name}</MenuItem> )}
        </Select>
      </FormControl>
    </Box>
      <Tree
        data={debugData}
        translate={translate}
        orientation={"vertical"}
        nodeSvgShape={svgSquare}
        circleRadius={5}
      />
    </div>
  );
} 
