import Tree from "react-d3-tree";
import axios from "axios"
const debugData = [
  {
    name: "Product Team",
    children: [
      {
        name: "Rizwana Yasmin",
        children: [
          {
            name: "Managment"
          },
          {
            name: "Shujaat Hussain",
