              {
                name: "Mangento"
              },
              {
                name: "FrontEnd Team",
                children: [
                  {
                    name: "Muhammad Shafiq",
                    children: [
                      {
                        name: "Ramzan"
                      },
                      {
                        name: "Curat"
                      }
                    ]
                  }
                ]
              }
            ]
          }
        ]
      }
    ]
  },
 
];

const containerStyles = {
  width: "100%",
  height: "100vh"
};

const svgSquare = {
  shape: "rect",
  shapeProps: {
    width: 20,
    height: 20,
    x: -10,
    y: -10
  }
};

export default class CenteredTree extends React.PureComponent {
  state = {
    records : "",
    headerRecords: "",
  };

  componentDidMount() {
    const dimensions = this.treeContainer.getBoundingClientRect();
    this.setState({
      translate: {
        x: dimensions.width / 10,
        y: dimensions.width / 3
      }
    });
    const fetchData = () => {
      return axios.post("http://172.31.42.23:8081/api/chs/v1/channel/listhierarchy", {})
            .then((response) => this.setState({records: response?.data?.data}))}
            fetchData()
            this.state.records.map(item => this.setState({headerRecords: item.channelName}))
    }
  render() {
    {console.log("hello", this.state.headerRecords)}
    return (
      <div style={containerStyles} ref={(tc) => (this.treeContainer = tc)}>
        <Tree
          data={debugData}
          translate={this.state.translate}
          orientation={"horizontal"}
          nodeSvgShape={svgSquare}
          circleRadius={5}
        />
      </div>
    );
  }
}
