import React, { useState, useEffect, useRef } from "react";
import Tree from "react-d3-tree";
import axios from "axios";
import defaultArray from './convertedArray';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import './custom-tree.css';

import {Grid} from '@mui/material';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import ImageIcon from '@mui/icons-material/Image';
import WorkIcon from '@mui/icons-material/Work';
import BeachAccessIcon from '@mui/icons-material/BeachAccess';
import { styled } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
export default function CenteredTree() {
  const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    //padding: theme.spacing(1),
    width: "100%",
    height: "900px",
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));
  const [channel, setChannel] = useState('Retail');

  const channels = defaultArray.filter(item => item.name === channel)
  // const styleValues = channels[0].children.map(item => item.dealerType);
  // console.log("valyesrrrr....", channels[0].children.map(item => item.dealerType))
  // function flatten(channels) {
  //   return channels? channels[0].reduce((result, item) => [
  //       ...result,
  //       { name: item.name },
  //       ...flatten(item.children)
  //   ], []) : [];
  // }

  // const valueFtn = flatten(defaultArray)
  // console.log("some", valueFtn);
  const debugData = [
    {
      name: "SALAM DMS",
      children: channels
    }

  ];
  const containerStyles = {
    width: "100%",
    height: "100vh"
  };

  const svgSquare = {
    shape: "rect",
    shapeProps: {
      width: 20,
      height: 20,
      x: -10,
      y: -10
    }
  };
  const handleChange = (event) => {
    setChannel(event.target.value);
  };
  const [translate, setTranslate] = useState({
    x: 0,
    y: 0
  })
  const treeContainer = useRef(null)
  useEffect(() => {
    const dimensions = treeContainer.current.getBoundingClientRect();
    setTranslate({
      x: dimensions.width / 10,
      y: dimensions.width / 3
    })
  }, []);
  // const straightPathFunc = (linkDatum, orientation) => {
  //   const { source, target } = linkDatum;
  //   console.log("source", source);
  //   console.log("target", target);
  //   console.log("linkDatum", linkDatum);
  // return orientation === 'horizontal'
  //   ? `M${source.y},${source.x}L${target.y},${target.x}`
  //   : `M${source.x},${source.y}L${target.x},${target.y}`;
  // };
  const color = (nodeDatum) => {
    console.log('nodeDatum', nodeDatum)
    return nodeDatum.dealerType === "pos_shop" ? "blue" : nodeDatum.dealerType === "distributor" || nodeDatum.dealerType === "sub_distributor" ? "yellow" : nodeDatum.name === "SALAM DMS" ? "green" : "black";
  }
  const renderRectSvgNode = ({ nodeDatum, toggleNode }) => (
    <g>
      <circle cx="5" cy="5" r="10" fill={color(nodeDatum)} />
      <text fill="black" strokeWidth="1" x="20" class="font">
        {nodeDatum.name}
      </text>
      {nodeDatum.attributes?.department && (
        <text fill="black" x="20" dy="20" strokeWidth="1">
          Department: {nodeDatum.attributes?.department}
        </text>
      )}
    </g>
  );
  return (

    <div style={containerStyles} ref={treeContainer}>
      <Box sx={{ maxWidth: 120 }}>
        <FormControl fullWidth>
          <InputLabel id="demo-simple-select-label">Select Channel</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={channel}
            label="Select Channel"
            onChange={handleChange}
          >

            {defaultArray.map(item => <MenuItem value={item.name}>{item.name}</MenuItem>)}
          </Select>
        </FormControl>
      </Box>
      <Grid container>
        <Grid item xs={3}>
          <Item>xs=8</Item>
        </Grid>
        <Grid item xs={9}>
          <Item>
          <Tree
        data={debugData}
        translate={translate}
        orientation={"vertical"}
        nodeSvgShape={svgSquare}

        // pathFunc={straightPathFunc}
        renderCustomNodeElement={renderRectSvgNode}
      />
          </Item>
        </Grid>
      </Grid>
    </div>
  );
}
